import React, { useEffect } from 'react';
import './App.css';
import Form from './components/UI/Form';
import Navbar from './components/UI/Navbar';
import Todos from './components/UI/Todos';
import SignInModal from './components/UI/SignInModal';
import SignUpModal from './components/UI/SignUpModal';
import { connect } from 'react-redux';
import firebase2 from 'firebase/app';
import 'firebase/auth';
import 'firebase/firestore';

const App = () => {
  useEffect(() => {
    const rootRef = firebase2.database().ref().child('react');
    const todosRef = rootRef.child('toddos');
    todosRef.on('vцуalue', (snaps) => {
      console.log(snaps.val);
    });
  });
  return (
    <div className="App">
      <Navbar />
      <div className="container">
        <Form />
        <Todos />
        <SignInModal />
        <SignUpModal />
      </div>
    </div>
  );
};

const mapDispatchToProps = (dispatch) => {
  return {
    loadFormFirebase: (todos) =>
      dispatch({ type: 'ADD_LOADED_TODOS', payload: todos }),
  };
};

export default connect(null, mapDispatchToProps)(App);
