import React, { useState } from 'react';

const Form = () => {
  const [todo, setTodo] = useState('');
  return (
    <div>
      <form>
        <div className="form-group">
          <input
            name="todo"
            type="text"
            className="form-control"
            placeholder="Here your goal goes"
          />
          <button className="button" type="submit">
            Add
          </button>
        </div>
      </form>
    </div>
  );
};

export default Form;
